     <div className="container my-3">
{/*      
      <Routes>
          <Route exact path="/about"
            element={<About />}/>

          <Route exact path="/" element ={ */}
          <TextForm heading="Enter the text to analyze below"mode={mode}/>
          {/* }>
            {/* <TextForm heading="Enter the text to analyze below"mode={mode}> */}
          {/* </Route>
      </Routes>
  //  */} 
    </div>
    {/* </Router> */}
     </>
  );
}

export default App;
