import React, {useState} from 'react'

export default function TextForm(props) {
  const handleUpClick = ()=>{
    let newText= text.toUpperCase();
    setText(newText)
  }
  const handleLoClick = ()=>{
    let newText= text.toLowerCase();
    setText(newText)
  }
  const handleClearClick = ()=>{
    let newText= ' ';
    setText(newText)
  }
  const handleCopy = ()=>{
    var text=document.getElementById("myBox");
    text.select();
    navigator.clipboard.writeText(text.value);
  }
  const handleExtraSpaces = ()=>{
   let newText=text.split(/[ ]+/);
   setText(newText.join(" "))
  }

  const handleOnChange = (event)=>{
    setText(event.target.value);
  }
  const [text, setText] = useState('enter text here');
  return (
    <>
    <div className='container my-4'style={{color: props.mode==='light'?'#042743':'white'}}>
      <h1>{props.heading}</h1>
      <div className="mb-3">
        <textarea className="form-control" value={text}style={{backgroundColor: props.mode==='light'?'white':'#042560',color: props.mode==='light'?'grey':'white'}} onChange={handleOnChange} id="myBox" rows="8"></textarea>
      </div>
      <button className="btn btn-primary mx-2" onClick={handleUpClick}>Convert to uppercase</button>
      <button className="btn btn-primary mx-2" onClick={handleLoClick}>Convert to lowercase</button>
      <button className="btn btn-primary mx-2" onClick={handleClearClick}>Clear Text</button>
      <button className="btn btn-primary mx-2" onClick={handleCopy}>Copy Text</button>
      <button className="btn btn-primary mx-2" onClick={handleExtraSpaces}>Remove extra space</button>
    </div>
    <div className='container my-4'style={{color: props.mode==='light'?'#042742':'white'}}>
      <h2>Your text summmary</h2>
      <p>{text.split(" ").length} words and {text.length} characters</p>
    </div>
    </>
  )
}
